mapper.update:
/* 글 업데이트 */
  update community_T set
  community_title = #{title},
  community_content = #{content}
  where community_no = #{no}

mapper.delete:
/* 글정보 삭제 */
Delete
From community_T
Where community_no = #{no}

mapper.update:
/* 커뮤니티 댓글 수정 */
    update c_comment_t set
    comment_text = #{text}
    where comment_no = #{no}

mapper.delete:
/* 커뮤니티 댓글 삭제 */
Delete
From c_comment_t
Where comment_no = #{no}
