mapper.select:
/* 내 농장 상세보기 */ SELECT my_farm_t.farm_no, user_id, serial_no, farm_name, farm_regidate, farm_text, file_path FROM my_farm_t left outer join myfarm_image_t on my_farm_t.farm_no = myfarm_image_t.farm_no
my_farm_t.farm_no = #{no} and user_id = #{id}
order by my_farm_t.farm_no asc LIMIT 1

/* 내 농장 전체 목록 */ SELECT my_farm_t.farm_no, user_id, serial_no, farm_name, farm_regidate, farm_text, file_path FROM my_farm_t left outer join myfarm_image_t on my_farm_t.farm_no = myfarm_image_t.farm_no
user_id = #{id} and (my_farm_t.farm_name like concat('%',#{keyword}, '%') or my_farm_t.farm_text like concat('%',#{keyword}, '%'))
order by my_farm_t.farm_no asc

mapper.update:
/* 내 농장 정보 수정 */
    update my_farm_t set
    farm_name = #{name},
    farm_text = #{text}
    where farm_no = #{no}

mapper.delete:
/* 내 농장 정보 삭제 */
Delete
From my_farm_t
Where farm_no = #{no}

mapper.select:
	/* 농장 이미지 검색 */
  SELECT  
      file_name
  FROM myfarm_image_t
  where farm_no = #{farm_no}

mapper.delete:
/* 내 농장 이미지 삭제 */
Delete
From myfarm_image_t
Where farm_no = #{farm_no}

mapper.insert:
 /* 내 농장 라이브 이미지 */
INSERT INTO live_image_t
(
    farm_no
)
VALUES
(
    #{farm_no}
)

mapper.select:
	/* 농장 라이브 이미지 */
  SELECT  
      file_name
  FROM live_image_t
  where farm_no = #{farm_no}

mapper.delete:
/* 내 농장 이미지 삭제 */
Delete
From live_image_t
Where farm_no = #{farm_no}
