mapper.select:
	/* 공지사항 글 상세보기 */
  SELECT  
      *
  FROM notice_t
  WHERE notice_no = #{no}
  LIMIT 1

/* 공지사항 글 전체 목록 */ SELECT notice_t.notice_no, notice_title, notice_author, notice_date, notice_content, notice_hit, notice_code, notice_user_id, count(comment_no) as 'comment_cnt' FROM notice_t left outer join n_comment_t on notice_t.notice_no = n_comment_t.notice_no
and notice_code = #{type} and notice_title like concat('%',#{keyword}, '%') or notice_content like concat('%',#{keyword}, '%')
group by notice_t.notice_no order by notice_code, notice_no desc LIMIT ${length} OFFSET ${start} ;

/* 공지사항 글 전체 목록 개수 */ 
 SELECT  
    count(notice_no) as 'list_cnt',
    notice_code
FROM notice_t

   and notice_code = #{type}
   and notice_title like CONCAT('%',#{keyword},'%') 
  OR notice_content like CONCAT('%',#{keyword},'%')

group by notice_t.notice_no;

mapper.update:
/* 공지사항 글 수정 */
    update notice_t set
    notice_title = #{title},
    notice_content = #{content}
    where notice_no = #{no}

mapper.delete:
/* 공지사항 삭제 */
Delete
From notice_t
Where notice_no = #{no}

mapper.update:
/* 공지사항 댓글 수정 */
    update n_comment_t set
    comment_text = #{text}
    where comment_no = #{no}

mapper.delete:
/* 공지사항 댓글 삭제 */
Delete
From n_comment_t
Where comment_no = #{no}
